import React, { useState, useEffect } from 'react';
import axios from 'axios';
import {
  AppShell,
  Burger,
  Group,
  Title,
  Text,
  Button,
  List,
  Stack,
  Progress,
  Container,
  Paper,
  Slider,
  ActionIcon,
  Accordion,
  Image,
  Flex,
  Box,
  rem,
  useMantineTheme,
  Divider,
  ScrollArea,
  Card,
  Center
} from '@mantine/core';
import { useDisclosure } from '@mantine/hooks';
import {
  IconPlayerPlay,
  IconPlayerPause,
  IconPlayerTrackNext,
  IconPlayerTrackPrev,
  IconVolume,
  IconVolumeOff,
  IconRepeat,
  IconMusic,
  IconChevronDown,
  IconChevronUp,
  IconAlbum,
  IconList,
  IconHistory
} from '@tabler/icons-react';

function App() {
  const [opened, { toggle }] = useDisclosure();
  const theme = useMantineTheme();
  const [playerState, setPlayerState] = useState({
    currentAlbum: '',
    currentTrack: '',
    albumTracks: [],
    volume: 70,
    isPlaying: false,
    repeatPlayback: false,
    logs: [],
    albumImage: null,
    position: 0,
    length: 0
  });

  const fetchPlayerState = async () => {
    try {
      const response = await axios.get('/api/player_state');
      setPlayerState({
        ...response.data,
        // If no album image is provided by the API, we'll use a placeholder
        albumImage: response.data.albumImage || null
      });
    } catch (error) {
      console.error('Error fetching player state:', error);
    }
  };

  useEffect(() => {
    fetchPlayerState();
    const interval = setInterval(fetchPlayerState, 5000);
    return () => clearInterval(interval);
  }, []);

  const handleAction = async (action, param = null) => {
    try {
      let url = `/api/${action}`;
      if (param !== null) {
        url += `/${param}`;
      }
      await axios.post(url);
      fetchPlayerState();
    } catch (error) {
      console.error(`Error with action ${action}:`, error);
    }
  };

  // Format time in mm:ss
  const formatTime = (seconds) => {
    if (!seconds) return '0:00';
    const mins = Math.floor(seconds / 60);
    const secs = Math.floor(seconds % 60);
    return `${mins}:${secs.toString().padStart(2, '0')}`;
  };

  return (
    <AppShell
      header={{ height: 60 }}
      navbar={{ width: 250, breakpoint: 'sm', collapsed: { mobile: !opened } }}
      padding="md"
      styles={{
        main: {
          background: theme.colorScheme === 'dark' ? theme.colors.dark[8] : theme.colors.gray[0],
        },
      }}
    >
      <AppShell.Header>
        <Group h="100%" px="md">
          <Burger opened={opened} onClick={toggle} hiddenFrom="sm" size="sm" />
          <Title order={3}>Music Player</Title>
        </Group>
      </AppShell.Header>

      <AppShell.Navbar p="md" bg={theme.colors.dark[6]}>
        <Stack spacing="xs">
          <Title order={4} c="white">Library</Title>
          <Button 
            leftSection={<IconAlbum size={16} />}
            variant="subtle"
            color="gray"
            fullWidth
            styles={{ root: { color: 'white', justifyContent: 'flex-start' } }}
          >
            Albums
          </Button>
          <Button 
            leftSection={<IconMusic size={16} />}
            variant="subtle"
            color="gray"
            fullWidth
            styles={{ root: { color: 'white', justifyContent: 'flex-start' } }}
          >
            Tracks
          </Button>
          <Divider my="sm" />

          <Title order={4} c="white">Now Playing</Title>
          {playerState.currentTrack ? (
            <Paper p="xs" withBorder bg={theme.colors.dark[5]}>
              <Text c="white" size="sm" fw={500}>{playerState.currentTrack}</Text>
              {playerState.currentAlbum && (
                <Text c="dimmed" size="xs">{playerState.currentAlbum}</Text>
              )}
            </Paper>
          ) : (
            <Text c="dimmed" size="sm">Nothing playing</Text>
          )}
        </Stack>
      </AppShell.Navbar>

      <AppShell.Main>
        <Container size="lg">
          <Flex
            direction={{ base: 'column', md: 'row' }}
            gap="xl"
            justify="flex-start"
            align="flex-start"
            mb="xl"
          >
            <Box w={{ base: '100%', md: 300 }}>
              <Card shadow="sm" p="lg" radius="md" withBorder>
                <Card.Section>
                  {playerState.albumImage ? (
                    <Image
                      src={playerState.albumImage}
                      height={300}
                      alt="Album cover"
                    />
                  ) : (
                    <Center p="xl" h={300} bg={theme.colors.dark[4]}>
                      <IconAlbum size={100} color={theme.colors.gray[2]} />
                    </Center>
                  )}
                </Card.Section>

                <Stack mt="md" mb="xs">
                  <Title order={3}>{playerState.currentTrack || 'No track playing'}</Title>
                  <Text size="sm" c="dimmed">{playerState.currentAlbum || 'No album'}</Text>
                </Stack>
              </Card>
            </Box>

            <Box style={{ flex: 1 }}>
              <Paper shadow="sm" p="lg" withBorder mb="md">
                <Title order={4} mb="md">Player Controls</Title>

                <Stack spacing="md">
                  <Group position="center">
                    <ActionIcon 
                      variant="subtle" 
                      color="blue" 
                      size="lg"
                      onClick={() => handleAction('prev_track')}
                    >
                      <IconPlayerTrackPrev style={{ width: rem(24), height: rem(24) }} />
                    </ActionIcon>

                    <ActionIcon 
                      variant="filled" 
                      color="blue" 
                      size="xl"
                      radius="xl"
                      onClick={() => handleAction('toggle_play_pause')}
                    >
                      {playerState.isPlaying ? 
                        <IconPlayerPause style={{ width: rem(30), height: rem(30) }} /> : 
                        <IconPlayerPlay style={{ width: rem(30), height: rem(30) }} />
                      }
                    </ActionIcon>

                    <ActionIcon 
                      variant="subtle" 
                      color="blue" 
                      size="lg"
                      onClick={() => handleAction('next_track')}
                    >
                      <IconPlayerTrackNext style={{ width: rem(24), height: rem(24) }} />
                    </ActionIcon>

                    <ActionIcon 
                      variant={playerState.repeatPlayback ? "filled" : "subtle"}
                      color="blue" 
                      size="lg"
                      onClick={() => handleAction('toggle_repeat_playback')}
                    >
                      <IconRepeat style={{ width: rem(24), height: rem(24) }} />
                    </ActionIcon>
                  </Group>

                  <Box>
                    <Progress 
                      value={(playerState.position / playerState.length) * 100 || 0} 
                      size="sm" 
                      radius="xl"
                    />
                    <Group position="apart" mt={5}>
                      <Text size="xs">{formatTime(playerState.position)}</Text>
                      <Text size="xs">{formatTime(playerState.length)}</Text>
                    </Group>
                  </Box>

                  <Group>
                    <ActionIcon 
                      variant="subtle" 
                      onClick={() => handleAction('set_volume', playerState.volume > 0 ? 0 : 70)}
                    >
                      {playerState.volume > 0 ? 
                        <IconVolume style={{ width: rem(18), height: rem(18) }} /> : 
                        <IconVolumeOff style={{ width: rem(18), height: rem(18) }} />
                      }
                    </ActionIcon>

                    <Slider
                      style={{ flex: 1 }}
                      value={playerState.volume}
                      onChange={(value) => handleAction('set_volume', value)}
                      size="sm"
                      radius="xl"
                      label={null}
                    />

                    <Text size="sm">{playerState.volume}%</Text>
                  </Group>
                </Stack>
              </Paper>

              {playerState.albumTracks.length > 0 && (
                <Paper shadow="sm" p="lg" withBorder mb="md">
                  <Group position="apart" mb="md">
                    <Title order={4}>Album Tracks</Title>
                    <IconList size={20} />
                  </Group>

                  <List spacing="xs" size="sm" center icon={
                    <Box w={24} h={24} bg="blue" style={{ borderRadius: '50%' }}>
                      <Center h="100%">
                        <IconMusic size={14} color="white" />
                      </Center>
                    </Box>
                  }>
                    {playerState.albumTracks.map((track, index) => (
                      <List.Item key={index}>
                        <Text>{track}</Text>
                      </List.Item>
                    ))}
                  </List>
                </Paper>
              )}

              <Accordion variant="contained">
                <Accordion.Item value="logs">
                  <Accordion.Control icon={<IconHistory size={20} />}>
                    <Title order={5}>Log Messages</Title>
                  </Accordion.Control>
                  <Accordion.Panel>
                    <ScrollArea h={200}>
                      <Box>
                        {playerState.logs.map((log, index) => (
                          <Text key={index} size="xs" ff="monospace">{log}</Text>
                        ))}
                      </Box>
                    </ScrollArea>
                  </Accordion.Panel>
                </Accordion.Item>
              </Accordion>
            </Box>
          </Flex>
        </Container>
      </AppShell.Main>
    </AppShell>
  );
}

export default App;
