  Card,
  Center,
  MantineProvider,
  Drawer
} from '@mantine/core';
import {
  IconPlayerPlay,
  IconPlayerPause,
  IconPlayerTrackNext,
  IconPlayerTrackPrev,
  IconVolume,
  IconVolumeOff,
  IconRepeat,
  IconMusic,
  IconHistory,
  IconList,
  IconAlbum,
  IconChevronUp
} from '@tabler/icons-react';
import { theme } from './theme';

function App() {
  // Set a fixed dark mode
  const colorScheme = 'dark';
  const mantineTheme = useMantineTheme();

  const [playerState, setPlayerState] = useState({
    currentAlbum: '',
    currentTrack: '',
    albumTracks: [],
    volume: 70,
    isPlaying: false,
    repeatPlayback: false,
    logs: [],
    albumImage: null,
    position: 0,
    length: 0
  });

  // State for log drawer
  const [logsDrawerOpen, setLogsDrawerOpen] = useState(false);

  const fetchPlayerState = async () => {
    try {
      const response = await axios.get('/api/player_state');
      setPlayerState({
        ...response.data,
        // If no album image is provided by the API, we'll use a placeholder
        albumImage: response.data.albumImage || null
      });
    } catch (error) {
      console.error('Error fetching player state:', error);
    }
  };

  useEffect(() => {
    fetchPlayerState();
    const interval = setInterval(fetchPlayerState, 5000);
    return () => clearInterval(interval);
  }, []);

  const handleAction = async (action, param = null) => {
    try {
      let url = `/api/${action}`;
      if (param !== null) {
        url += `/${param}`;
      }
      await axios.post(url);
      fetchPlayerState();
    } catch (error) {
      console.error(`Error with action ${action}:`, error);
    }
  };

  // Format time in mm:ss
  const formatTime = (seconds) => {
    if (!seconds) return '0:00';
    const mins = Math.floor(seconds / 60);
    const secs = Math.floor(seconds % 60);
    return `${mins}:${secs.toString().padStart(2, '0')}`;
  };

  // Player controls component that will be docked at the bottom
  const PlayerControls = () => (
    <Paper 
      shadow="sm" 
      p={{ base: 'xs', md: 'md' }}
      withBorder 
      style={{
        background: mantineTheme.colors.dark[7],
        position: 'fixed',
        bottom: 0,
        left: 0,
        right: 0,
        zIndex: 100,
      }}
    >
      <Stack spacing={{ base: 'xs', md: 'sm' }} align="center">
        {/* Progress bar with times on either end */}
        <Box style={{ width: '100%', maxWidth: '800px' }}>
          <Progress 
            value={(playerState.position / playerState.length) * 100 || 0} 
            size="sm" 
            radius="xl"
            color="orange"
          />
          <Group position="apart" mt={5}>
            <Text size="xs">{formatTime(playerState.position)}</Text>
            <Text size="xs">{formatTime(playerState.length)}</Text>
          </Group>
        </Box>

        <Flex 
          justify="center" 
          align="center" 
          style={{ width: '100%' }}
          direction={{ base: 'column', sm: 'row' }}
          gap={{ base: 'xs', sm: 'md' }}
        >
          {/* Track info on the left - hidden on very small screens */}
          <Flex 
            align="center" 
            gap="md" 
            style={{ flex: 1, justifyContent: 'flex-start' }}
            display={{ base: 'none', sm: 'flex' }}
          >
            <Box w={50} h={50} style={{ minWidth: 50 }}>
              {playerState.albumImage ? (
                <Image
                  src={playerState.albumImage}
                  height={50}
                  width={50}
                  alt="Album thumbnail"
                  radius="sm"
                />
              ) : (
                <Center h={50} bg={mantineTheme.colors.dark[5]} style={{ borderRadius: '4px' }}>
                  <IconAlbum size={30} color={mantineTheme.colors.gray[2]} />
                </Center>
              )}
            </Box>
            <Box>
              <Text size="sm" weight={500} lineClamp={1}>
                {playerState.currentTrack || 'No track playing'}
              </Text>
              <Text size="xs" c="dimmed" lineClamp={1}>
                {playerState.currentAlbum || 'No album'}
              </Text>
            </Box>
          </Flex>

          {/* Playback controls in the center */}
          <Group position="center" spacing="md" style={{ flex: 1 }}>
            <ActionIcon 
              variant="subtle" 
              color="orange" 
              size="md"
              onClick={() => handleAction('prev_track')}
            >
              <IconPlayerTrackPrev style={{ width: rem(20), height: rem(20) }} />
            </ActionIcon>

            <ActionIcon 
              variant="filled" 
              color="orange" 
              size="lg"
              radius="xl"
              onClick={() => handleAction('toggle_play_pause')}
            >
              {playerState.isPlaying ? 
                <IconPlayerPause style={{ width: rem(24), height: rem(24) }} /> : 
                <IconPlayerPlay style={{ width: rem(24), height: rem(24) }} />
              }
            </ActionIcon>

            <ActionIcon 
              variant="subtle" 
              color="orange" 
              size="md"
              onClick={() => handleAction('next_track')}
            >
              <IconPlayerTrackNext style={{ width: rem(20), height: rem(20) }} />
            </ActionIcon>

            <ActionIcon 
              variant={playerState.repeatPlayback ? "filled" : "subtle"}
              color="orange" 
              size="md"
              onClick={() => handleAction('toggle_repeat_playback')}
            >
              <IconRepeat style={{ width: rem(20), height: rem(20) }} />
            </ActionIcon>
          </Group>

          {/* Right side controls */}
          <Flex 
            align="center" 
            gap="md" 
            style={{ flex: 1, justifyContent: 'flex-end' }}
          >
            {/* Volume controls - hidden on very small screens */}
            <Group 
              spacing="xs" 
              display={{ base: 'none', sm: 'flex' }}
            >
              <ActionIcon 
                variant="subtle" 
                color="orange"
                onClick={() => handleAction('set_volume', playerState.volume > 0 ? 0 : 70)}
              >
                {playerState.volume > 0 ? 
                  <IconVolume style={{ width: rem(18), height: rem(18) }} /> : 
                  <IconVolumeOff style={{ width: rem(18), height: rem(18) }} />
                }
              </ActionIcon>

              <Slider
                style={{ width: '100px' }}
                value={playerState.volume}
                onChange={(value) => handleAction('set_volume', value)}
                size="sm"
                radius="xl"
                label={null}
                color="orange"
              />
            </Group>

            {/* Logs button */}
            <ActionIcon 
              variant="subtle" 
              color="gray" 
              onClick={() => setLogsDrawerOpen(true)}
              title="Show logs"
            >
              <IconChevronUp style={{ width: rem(18), height: rem(18) }} />
            </ActionIcon>
          </Flex>
        </Flex>
      </Stack>
    </Paper>
  );

  return (
    <MantineProvider theme={{ ...theme, colorScheme }} withGlobalStyles withNormalizeCSS>
      <AppShell
        padding="md"
        header={{ height: 60 }}
        styles={{
          main: {
            background: mantineTheme.colors.dark[8],
            paddingBottom: { base: '80px', sm: '120px' }, // Responsive padding for the player controls
          },
        }}
      >
        <AppShell.Header style={{ 
          background: mantineTheme.colors.dark[8],
          borderBottom: '1px solid',
          borderColor: mantineTheme.colors.dark[6]
        }}>
          {/* Center title with flex layout */}
          <Flex 
            h="100%" 
            justify="center" 
            align="center"
          >
            <Title order={3} c="white">SLAB ONE</Title>
          </Flex>
        </AppShell.Header>

        <AppShell.Main>
          <Container size="lg" py="md">
            {/* Main content area */}
            <Flex
              direction="column"
              gap="xl"
              justify="flex-start"
              align="stretch"
              mb="xl"
            >
              {/* Album and Tracks section */}
              <Flex
                direction={{ base: 'column', md: 'row' }}
                gap="xl"
                justify="flex-start"
                align={{ base: 'stretch', md: 'flex-start' }}
                mb="xl"
              >
                {/* Album cover on the left */}
                <Box w={{ base: '100%', md: 300 }} maw={{ md: 300 }}>
                  <Card shadow="sm" p="lg" radius="md" withBorder>
                    <Card.Section>
                      {playerState.albumImage ? (
                        <Image
                          src={playerState.albumImage}
                          height={{ base: 250, md: 300 }}
                          alt="Album cover"
                          fit="contain"
                        />
                      ) : (
                        <Center p="xl" h={{ base: 250, md: 300 }} bg={mantineTheme.colors.dark[4]}>
                          <IconAlbum size={100} color={mantineTheme.colors.gray[2]} />
                        </Center>
                      )}
                    </Card.Section>

                    <Stack mt="md" mb="xs">
                      <Title order={3}>{playerState.currentTrack || 'No track playing'}</Title>
                      <Text size="sm" c="dimmed">{playerState.currentAlbum || 'No album'}</Text>
                    </Stack>
                  </Card>
                </Box>

                {/* Tracks list on the right */}
                <Box style={{ flex: 1 }}>
                  {playerState.albumTracks.length > 0 ? (
                    <Paper shadow="sm" p="lg" withBorder mb="md">
                      <Group position="apart" mb="md">
                        <Title order={4}>Album Tracks</Title>
                        <IconList size={20} />
                      </Group>

                      <List spacing="xs" size="sm" center icon={
                        <Box w={24} h={24} bg={mantineTheme.colors.orange[6]} style={{ borderRadius: '50%' }}>
                          <Center h="100%">
                            <IconMusic size={14} color="white" />
                          </Center>
                        </Box>
                      }>
                        {playerState.albumTracks.map((track, index) => (
                          <List.Item key={index}>
                            <Text>{track}</Text>
                          </List.Item>
                        ))}
                      </List>
                    </Paper>
                  ) : (
                    <Paper shadow="sm" p="lg" withBorder mb="md">
                      <Center p="xl">
                        <Text c="dimmed">No tracks available</Text>
                      </Center>
                    </Paper>
                  )}
                </Box>
              </Flex>
            </Flex>
          </Container>
        </AppShell.Main>

        {/* Player Controls docked at the bottom */}
        <PlayerControls />

        {/* Logs Drawer that slides up from the bottom */}
        <Drawer
          opened={logsDrawerOpen}
          onClose={() => setLogsDrawerOpen(false)}
          position="bottom"
          size="md"
          title={
            <Group>
              <IconHistory size={20} />
              <Title order={5}>Log Messages</Title>
            </Group>
          }
          styles={{
            header: {
              background: mantineTheme.colors.dark[7],
              borderBottom: `1px solid ${mantineTheme.colors.dark[5]}`,
            },
            body: {
              background: mantineTheme.colors.dark[7],
            }
          }}
        >
          <ScrollArea h={300}>
            <Box p="md">
              {playerState.logs.length > 0 ? (
                playerState.logs.map((log, index) => (
                  <Text key={index} size="xs" ff="monospace" mb={5}>{log}</Text>
                ))
              ) : (
                <Center>
                  <Text c="dimmed">No log messages available</Text>
                </Center>
              )}
            </Box>
          </ScrollArea>
        </Drawer>
      </AppShell>
    </MantineProvider>
  );
}

export default App;
